import React, { Component } from 'react';
import handleViewport from 'react-in-viewport';

class MySectionBlock extends Component {
  getStyle() {
    const { inViewport, enterCount } = this.props;
    //Fade in only the first time we enter the viewport
    if (inViewport && enterCount === 1) {
      return { WebkitTransition: 'opacity 0.15s ease-in-out' };
    } else if (!inViewport && enterCount < 1) {
      return { WebkitTransition: 'none', opacity: '0' };
    } else {
      return {};
    }
  }

  render() {
    const { enterCount, leaveCount } = this.props;
    return (
      <section>
        <div className="content" style={this.getStyle()}>
        <div className='percentageBars'>

                <div className="percentageBarsShell">
                    <div className="circle-wrap">
                        <div className="circle"> 
                            <div className="mask full">
                                <div className="fill"></div>
                            </div>

                            <div className="mask half">
                                <div className="fill"></div>
                            </div>

                            <div className="inside-circle">
                                70%
                            </div>
                        </div>
                    </div>
                    <div className="percentageBarsShellInfo">
                        of online experiences begin
                        <br /> 
                         with a search engine
                    </div>
                </div>

                <div className="percentageBarsShell">
                    <div className="circle-wrap">
                        <div className="circle"> 
                            <div className="mask full">
                                <div className="fill"></div>
                            </div>

                            <div className="mask half">
                                <div className="fill"></div>
                            </div>

                            <div className="inside-circle">
                                70%
                            </div>
                        </div>
                    </div>
                    <div className="percentageBarsShellInfo">
                        of people never scroll past
                        <br /> 
                        the first page of results
                        </div>
                </div>

                <div className="percentageBarsShell">
                    <div className="circle-wrap">
                        <div className="circle"> 
                            <div className="mask full">
                                <div className="fill"></div>
                            </div>

                            <div className="mask half">
                                <div className="fill"></div>
                            </div>

                            <div className="inside-circle">
                                70%
                            </div>
                        </div>
                    </div>
                    <div>
                        <div className="percentageBarsShellInfo">
                            of users research a product
                            <br /> 
                            before making a purchase
                        </div>
                    </div>
                </div>

            </div>
          <p>{`Enter viewport: ${enterCount} times`}</p>
          <p>{`Leave viewport: ${leaveCount} times`}</p>
        </div>
      </section>
    );
  }
}
const MySection = handleViewport(MySectionBlock, { rootMargin: '-1.0px' });

// const PercentageBars = () => {
